dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		responsive: {
			details: {
				display: $.fn.dataTable.Responsive.display.modal,
				renderer: function ( api, rowIdx, columns ) {
					var data = $.map( columns, function ( col, i ) {
						return '<tr>'+
								'<td>'+col.title+':'+'</td> '+
								'<td>'+col.data+'</td>'+
							'</tr>';
					} ).join('');

					return $('<div/>')
						.append( '<h2>Details for '+columns[0].data+' '+columns[1].data+'</h2>' )
						.append( $('<table/>').append( data ) );
				}
			}
		}
	} );
} );
